#!/usr/bin/env python# -*- coding: utf-8 -*-# @Date    : 2018-04-02 19:11:40# @Author  : mohailang (1198534595@qq.com)from plotly.graph_objs import *import numpy as npimport pandas as pdimport plotly.offlinedef Map1():    # 读取数据文件    myFile = pd.read_csv('directory.csv')    # 处理缺省值    myFile = myFile.fillna("None")    # 读取文件各项数据    lat = np.array(myFile["Latitude"])    lon = np.array(myFile["Longitude"])    storeNum = myFile["Store Number"]    storeName = myFile["Store Name"]    Type = myFile["Ownership Type"]    streetAddress = myFile["Street Address"]    city = myFile["City"]    state = myFile["State/Province"]    country = myFile["Country"]    postCode = myFile["Postcode"]    phoneNumber = myFile["Phone Number"]    timeZone = myFile["Timezone"]    # 文本信息    myText = (        "StoreNum: " + storeNum + '</br>' +        "StoreName: " + storeName + '</br>' +        "OwnershipType: " + Type + '</br>' +        "StreetAddress: " + streetAddress + '</br>' +        "City: " + city + '</br>' +        "State: " + state + '</br>' +        "Country: " + country + '</br>' +        "Postcode: " + postCode + '</br>' +        "phoneNumber: " + phoneNumber + '</br>' +        "TimeZone: " + timeZone    )    data = Data([        Scattermapbox(            lat=lat,            lon=lon,            mode='markers',            marker=Marker(                size=14,                # color="rgb(255,0,0)"            ),            text=myText,        )    ])    layout = Layout(        autosize=True,        hovermode='closest',        mapbox=dict(            accesstoken="pk.eyJ1IjoibW9oYWlsYW5nIiwiYSI6ImNqZm93cGs5bDF3OXMyeG1zdGhuejBoNTIifQ.fouiU5hKtls0ohPA7LHJEA",            bearing=0,            center=dict(                lat=38.92,                lon=-77.07            ),            pitch=0,            zoom=10        ),    )    fig = dict(data=data, layout=layout)    plotly.offline.plot(fig, filename='directory1.html')